$(() => {

  //missedBalloons will contain the number of balloons within the top row
  let missedBalloons = 2;
  //startArea is an array of the section of the board where the balloons will generate
  let $slowBallons = $('.slow-area');
  let $mediumBallons = $('.medium-area');
  let $fastBallons = $('.fast-area');
  const pop = $('li');

  //An if..else statment will check whether the top row contains three ballons. So long as it doesn't balloons will be generated by putting their ID into a random start area's square (picked by math.random)
  function generateBalloons() {
    if (missedBalloons < 3) {
      $slowBallons.Math.floor(Math.random()*$slowBallons.length).attr('id', 'slow');
      // $slowBallons.attr('id', 'slow');
      // } else if (missedBalloons <= 2) {
      //   $mediumBallons.attr('id', 'medium-area');
      // } else
      // $fastBallons.attr('id', 'fast-area');
    }
  }
  generateBalloons();

  //A mouseover event will take the balloon's ID away, therefore making it pop
  pop.mouseover(function() {
    $(this).removeAttr('id');
  });

  //An animation wil make the balloons travel up the screen

  //A for loop will check the top to see if any balloons have been missed. If they have it will increase missedBalloons by one each time




});
